mapper.select:
select sq_lib.question_lib_id, sq_lib.question_lib_name, sq_user_lib.user_name, sq_tag.lib_tag_name from sq_question_lib sq_lib left join sq_user_question_lib sq_user_lib on sq_user_lib.question_lib_id = sq_lib.question_lib_id left join sq_lib_tag sq_tag on sq_lib.question_lib_tag_id = sq_tag.lib_tag_id where sq_lib.question_lib_private = 0 and sq_lib.question_lib_id > #{total}
and sq_lib.question_lib_tag_id = #{typeId}
order by sq_lib.question_lib_id limit #{limit}
